import styled from "styled-components";

export const Row = styled.div<{ gap?: number }>`
	display: flex;
	flex-direction: row;
`;

export const Column = styled.div<{ gap?: number }>`
	display: flex;
	flex-direction: column;
	gap: ${({ gap }) => gap + "px"};
`;
