import React, { ReactElement } from "react";
import styled from "styled-components";
import "../css/App.css";

const WhiteButton = styled.button<{ float?: string | undefined }>`
	background-color: rgb(255, 255, 255, 0.5);
	border: 0.25px solid white;
	border-radius: 5px;
	height: 50px;
	width: 100px;
	cursor: pointer;
	float: ${({ float }) => float};

	&:hover {
		border: 0.5px solid white;
	}

	color: #aa4586;
	font-family: myFont;
	font-weight: bold;
	font-size: 20px;
	text-transform: uppercase;
`;

export const PinkButton = styled.button`
	border: 1px solid white;
	border-radius: 5px;
	padding: 5px 10px 5px 10px;
	color: white;
	background: #c46ea6;
	width: 200px;
	cursor: pointer;
	color: white;
`;

type ButtonProps = {
	style: string;
	to: string;
	text: string;
	float?: string;
};

export function Button({ style, to, text, float }: ButtonProps) {
	return (
		<div>
			<a href={to} target={style === "white" ? "_self" : "_blank"}>
				{style === "white" ? (
					<WhiteButton float={float}>{text}</WhiteButton>
				) : (
					<PinkButton>{text}</PinkButton>
				)}
			</a>
		</div>
	);
}

const ButtonContainer = styled.div<{ align?: string | undefined }>`
	display: flex;
	gap: 20px;
	justify-content: ${({ align }) => align};
`;

type ButtonRowProps = {
	children: ReactElement[];
	align?: string;
};

export function ButtonRow({ children, align }: ButtonRowProps) {
	return <ButtonContainer align={align}>{children}</ButtonContainer>;
}
